import React, { useState } from "react";

import {saveShippingAddress} from "../services/shippingService";

const STATUS = {
  IDLE: "IDLE",
  SUBMITEED:"SUBMITTED",
  SUBMITTING: "SUBMITTING",
  COMPLETED: "COMPLETED"
}

// Declaring outside component to avoid recreation on each render
const emptyAddress = {
  city: "",
  country: "",
};

//Derived state

export default function Checkout({ cart, emptyCart }) {
  const [address, setAddress] = useState(emptyAddress);
  const [status, setStatus] = useState(STATUS.IDLE);
  const [saveError, setSaveError] = useState(null);

  //Dervied State
  const errors = getErrors(address);
  const isValid = Object.keys(errors).length === 0;


  function getErrors(adress){
    const result = {};
    if (!address.city) return result.city = "City adress is required";
    if (!address.country) return result.city = "Country add ress is required";
    return result;

  }

  function handleChange(e) {
      e.persist();
      setAddress((currAddress)=> {return {...currAddress,[e.target.id]: e.target.value} });
  }


  function handleBlur(event) {
    // TODO
  }

  async function handleSubmit(event) {
    event.prevent.default(); 
    setStatus(STATUS.SUBMITTING);
    if (isValid){
    try{
      await saveShippingAddress(address);
      emptyCart();
      setStatus(STATUS.COMPLETED);
    }
    catch(e){
      setSaveError(e);
    }
  }
  }

  if (saveError) throw saveError;
  if (status === STATUS.COMPLETED){
    return <h1>Thanks for shopping! </h1>;
  }
  return (
    <>
      <h1>Shipping Info</h1>
      {!isValid && status === STATUS.SUBMITEED && (
        <div role="alert">
        <p>Please fix the following errors</p>
        <ul>
          {Object.keys(errors).map(key => {
            return <li key={key}>{errors[key]}</li>
          })}
        </ul>
        </div>
      ) }
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="city">City</label>
          <br />
          <input
            id="city"
            type="text"
            value={address.city}
            onBlur={handleBlur}
            onChange={handleChange}
          />
        </div>

        <div>
          <label htmlFor="country">Country</label>
          <br />
          <select
            id="country"
            value={address.country}
            onBlur={handleBlur}
            onChange={handleChange}
          >
            <option value="">Select Country</option>
            <option value="China">China</option>
            <option value="India">India</option>
            <option value="United Kingdom">United Kingdom</option>
            <option value="USA">USA</option>
          </select>
        </div>

        <div>
          <input
            type="submit"
            className="btn btn-primary"
            value="Save Shipping Info"
            disabled={status=== STATUS.SUBMITTING}
          />
        </div>
      </form>
    </>
  );
}
